import React, {useState} from "react";

const Counter1 =() =>{
    const [count,setCount] = useState(0);
    const [buttonClick, setButtonClick] = useState(false);

    const increment= () =>{
        setCount(count + 1);
        setButtonClick(true);
    }
    const decrement = () =>{
        setCount(count - 2);
        setButtonClick(true);
    }
    return(
        <div>
            <p>Count: {count}</p>
            <button>Increment by 1 {increment}</button>
            <button>Decrement by 2 {}</button>
        </div>

    )

}
export default Counter1;
